import { useState } from 'react'


function Create({ supabase }) { 
  const [post, setPost] = useState({ title: "", author: ""})

  const handleChange = (event) => {
    const {name, value} = event.target;
    console.log("Name: ", name, " value: ", value);
    setPost( (prev) => {
        return {
            ...prev,
            [name]:value,
        }
    })
}

  const createBook = async () => {
    const { data, error } = await supabase.from("Books Demo").insert([{ title: post.title, author: post.author }]);
  }



  return (
    <div className="Create"> 
      <form>
        <label> Title </label>
          <input type="text" name="title" value={post.title} onChange={handleChange} />

        <label>Author</label>
        <input type="text" name="author" value={post.author} onChange={handleChange} />

        <input type="submit" value="submit" onClick={createBook}></input>
      </form>
    </div>
  );
}

export default Create;
